import React from 'react';

class ProductsPage extends React.Component {
    render() {
        return (
            <Navbar />
        )
    }
}

class Navbar extends React.Component {
    render () {
        return (
            <div>
                <header>
                    <nav className="container justify-content-end navbar navbar-expand-lg navbar-light bg-light">
                        <a className="navbar-brand" href="./index.html"><i className="flaticon-gym"></i></a>
                        <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarText"
                            aria-controls="navbarText" aria-expanded="false" aria-label="Toggle navigation">
                            <span className="navbar-toggler-icon"></span>
                        </button>
                        <div className="collapse navbar-collapse" id="navbarText">
                            <ul className="navbar-nav ml-auto">
                                <li className="nav-item hover">
                                    <a className="nav-link" href="./index.html">Home <span className="sr-only">(current)</span></a>
                                </li>
                                <li className="nav-item active hover">
                                    <a className="nav-link" href="./product.html">Product</a>
                                </li>
                                <li className="nav-item hover">
                                    <a className="nav-link" href="./contact.html">Contact</a>
                                </li>
                            </ul>
                        </div>
                        </nav>
                    </header>
                    <ProductHero />
            </div>
        )
    }
}

class ProductHero extends React.Component {
    render () {
        return (
            <div>
                <section className="container-fluid hero">
                    <div className="hero-product">
                        <h1>Our <span className="yellow-text">Stock</span></h1>     
                    </div>
                </section>
                <ProductList />
            </div>
        )
    }
}

class ProductList extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            productsList: []
        };
    }

    componentDidMount() {
        fetch('./data/products.json')
            .then((res) => res.json())
            .then(
                (data) => {
                    this.setState({
                        isLoaded: true,
                        productsList: data.products
                    });
                },
                (error) => {
                    this.setState({
                        isLoaded: true,
                        error
                    })
                });



    }
    render() {
        const { error, isLoaded, productsList } = this.state;

        if (error) {
            return <div>Error: {error.message}</div>;
        } else if (!isLoaded) {
            return <div>Loading...</div>;
        } else {
            return (
                <div>
                <div className="container-fluid products-container"> 
                    <div className="row">
                        {productsList.map((product, index) => (
                            <div key="product.name" className="col-md-3 product-column">
                                {index}
                                <img className="img-fluid" src={product.imageSource} alt={product.name} />
                                <p className="text-left">
                                    <span className="product-title">{product.name}</span> 
                                    <span className="product-price">{product.price}</span>
                                    <span className="d-block item-description">{product.category}</span>
                                </p>
                            </div>
                        ))}
                    </div>

                </div>
                <Footer />
                </div>
            );
        }
    }
}

class Footer extends React.Component {
    render() {
        return (
            <footer className="page-footer">
      <div className="container">
        <div className="row">
          <div className="col-md-12 py-5">
            <div className="mb-5 d-flex justify-content-center footerIcons">
              <a href="https://www.facebook.com/" target="_blank" className="fb-ic">
                <i className="fab fa-facebook-f fa-lg white-text mr-md-5 mr-3 fa-2x iconColor"> </i>
              </a>

              <a href="https://twitter.com/?lang=en" target="_blank" className="tw-ic">
                <i className="fab fa-twitter fa-lg white-text mr-md-5 mr-3 fa-2x iconColor"> </i>
              </a>

              <a href="https://www.linkedin.com/" target="_blank" className="li-ic">
                <i className="fab fa-linkedin-in fa-lg white-text mr-md-5 mr-3 fa-2x iconColor"> </i>
              </a>

              <a href="https://www.instagram.com/" target="_blank" className="ins-ic">
                <i className="fab fa-instagram fa-lg white-text mr-md-5 mr-3 fa-2x iconColor"> </i>
              </a>

              <a href="https://www.pinterest.com/" target="_blank" className="pin-ic">
                <i className="fab fa-pinterest fa-lg white-text fa-2x iconColor"> </i>
              </a>
            </div>
          </div>
        </div>
      </div>
      <div className="footer-copyright text-center py-3 copyrightText">
        <p>© 2019 Copyright</p>
      </div>
    </footer>
        )
    }
}
export default ProductsPage;
